!function (a) {
    a(document).on("click.bootstrap-toggle", "[data-toggle^=toggle]", function (c) {
        var b = a(this);
        var d = a(this).find("input[type=checkbox]");
        if (b.hasClass("off")) {
        	if(b.hasClass("multichoice_radio")){
        		b.attr("class", "multichoice_radio toggle " + b.find(".toggle-on").attr("class").replace(/toggle-on/g, ""));
        	}
        	else if(b.hasClass("no_right_answer")){        		
        		b.attr("class", "no_right_answer toggle " + b.find(".toggle-on").attr("class").replace(/toggle-on/g, ""));
        	}
        	else if(b.hasClass("quick_preview")){        		
        		b.attr("class", "quick_preview btn-small toggle " + b.find(".toggle-on").attr("class").replace(/toggle-on/g, ""));
        	}
        	else if(b.hasClass("no_right_answer_toggle")){
        		var checkbox = b.find("input");
        		var array = checkbox.attr("id").split("_");
        		b.attr("class", "no_right_answer_toggle no_right_answer_toggle_"+array[2]+" toggle " + b.find(".toggle-on").attr("class").replace(/toggle-on/g, ""));
        	}
        	else if(b.hasClass("button_show_right_answer")){
        		var checkbox = b.find("input");
        		var array = checkbox.attr("id").split("_");
        		b.attr("class", "button_show_right_answer button_show_right_answer_"+array[2]+" toggle " + b.find(".toggle-on").attr("class").replace(/toggle-on/g, ""));
        	}
        	else{
        		b.attr("class", "toggle " + b.find(".toggle-on").attr("class").replace(/toggle-on/g, ""));
        	}
            d.prop("checked", true);
            b.removeClass("off");        	
        } else {
        	if(b.hasClass("multichoice_radio")){
        		b.attr("class", "multichoice_radio toggle " + b.find(".toggle-off").attr("class").replace(/toggle-off/g, ""));
        	}
        	else if(b.hasClass("no_right_answer")){
        		b.attr("class", "no_right_answer toggle " + b.find(".toggle-off").attr("class").replace(/toggle-off/g, ""));
        	}
        	else if(b.hasClass("quick_preview")){
        		b.attr("class", "quick_preview btn-small toggle " + b.find(".toggle-off").attr("class").replace(/toggle-off/g, ""));
        	}
        	else if(b.hasClass("no_right_answer_toggle")){
        		var checkbox = b.find("input");
        		var array = checkbox.attr("id").split("_");
        		b.attr("class", "no_right_answer_toggle no_right_answer_toggle_"+array[2]+" toggle " + b.find(".toggle-off").attr("class").replace(/toggle-off/g, ""));
        	}
        	else if(b.hasClass("button_show_right_answer")){
        		var checkbox = b.find("input");
        		var array = checkbox.attr("id").split("_");
        		b.attr("class", "button_show_right_answer button_show_right_answer_"+array[2]+" toggle " + b.find(".toggle-off").attr("class").replace(/toggle-off/g, ""));
        	}
        	else{
        		b.attr("class", "toggle " + b.find(".toggle-off").attr("class").replace(/toggle-off/g, ""));
        	}
            d.prop("checked", false);
            b.addClass("off");
        }
    });
}(window.jQuery);